package novice;

//more patterns using print and println
public class Patterns3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("*");
		System.out.println("***");
		System.out.println("*****");
		System.out.print("****");
		System.out.println("**");
		
		System.out.printf("%s%n%s%n%s%n", "*", "***", "*****");
	}

}
